/************************************
 * USABILITY
 ************************************/
.link a:focus {
    /* highlighted tab-selection */
    background-color: rgba(97, 164, 249, 0.2) !important;
}

/************************************
 * DISTRACTION FREE
 ************************************/
.add-bookmark,
#navigation,
#new-category,
#footer {
    display: none;
}
.sortable {
    margin-top: 4em;
}
#sortable1 {
    width: 28em;
}
#sortable2 {
    width: 28em;
}
#messages {
    top: 0em;
}
.group {
    padding-bottom: 0em;
}
.portlet-header {
    margin-top: 0.4em !important;
}
#filter {
    margin-left: 1.5em;
    top: 0.9em;
    left: 0;
    height: 1.6em;
}
#top-buttons {
    left: 45.5em;
}
::-webkit-scrollbar {
    display: none;
}

/************************************
 * DARK THEME
 ************************************/
body,
#main {
    background-color: #313434;
}
#messages {
    background-color: #292929;
}
.link a,
.portlet-header {
    color: #E2E4E6 !important;
}
.button-style {
    background-color: #313434 !important;
    border: 1px solid #81807F !important;
}
#filter {
    background-color: #313434 !important;
    color: #E2E4E6 !important;
}

/************************************************************************
* GLOBAL DEFINITIONS 
************************************************************************/

/*
    Light blue | 87AFDF
    Light red | D78787
    Neon green | AFD787
    Cyan | AFDFDF
    Magenta | DFAFDF
    Yellow | FFFFAF
    White | FFFFFF
    Super light grey | E2E4E6
    Even lighter grey | 81807F
    Lighter grey | 696969
    Basic grey | 393F3F
    Darker grey | 313434
    Even darker grey | 292929
    Black | 000
    Default windows grey | F0F0F0
    */
* {
    /* Even darker grey */
    --bg: #292929;
    /* Basic grey */
    --bg-medium: #393F3F;
    /* Light grey */
    --bg-light: #696969;
    /* Light blue */
    --hlight-1: #87AFDF;
    /* Light red */
    --hlight-2: #D78787;
    /* Yellow */
    --hlight-3: #FFFFAF;
    /* White */
    --fg: #F0F0F0;
    background-color: var(--bg) !important;
    color: var(--fg) !important;
}

/************************************************************************
* MAIL VIEW
************************************************************************/

/* Search bar & mail list headers */
#divBasicSearch *,
#divHeaders * {
    color: var(--hlight-1) !important;
}

/* Outlook logo top left */
#divBrandLogo {
    display: none;
}

/* Mail composition: To/From/CC/BCC fields..  */
.rwRRO,
#spnActions,
#spnSent,
#divConvTopic {
    color: var(--hlight-1) !important;
}

/* Mail composition fields */
.rwW,
.rwRR,
.txtField,
.txtWell {
    border: none !important;
    background-color: var(--bg-medium) !important;
}

/* Grey background on mail composition headers */
#spnR {
    background-color: var(--bg-medium) !important;
    color: var(--fg) !important;
}

/* Add a separator between mail headers and mail body */
#divBdy {
    border-top: 1px solid var(--bg-light)
}

/* Remove all the bitmap-based gradients */
.vlvExpSel,
.divPrtBkSel,
.nbMnuItm,
.nbMnuItmN,
.nbHiLt,
#divNvDrag,
#divInfobarColor,
#divDividerTile,
.sprites-hdiv-r-png,
#divDraftsBkMask,
#divDraftsBk,
#spnTrNdCnt,
#spnFldrNm,
.trNd,
#spnFldrNm,
#spnC,
#spnCV,
#spnUC,
#spnTrNdCnt,
#spnFldrNm {
    background: none !important;
}

/* No border over main panels */
#divNvPn,
#divLVContainer {
    border: none;
}

/* Mail composition format buttons */
#imgNvDrag,
#divNvDrag,
#divFmtBr,
#divIB,
#divInfobarColor,
.fbBtn,
.fbBtnGrp {
    border: none !important;
}

/* Draft items: Red borders */
#divExp.draftItem,
#divClps.draftItem {
    border: 1px solid var(--hlight-2);
}

/* Highlighting of unread messages and folders with unread messages */
.ur,
.ur *,
.fufw {
    color: var(--hlight-2) !important;
}

/* Highlighting of currently selected mail or currently selected mail folder */
.sel *,
#sr.sel,
#vr.sel,
#divTrNdEff.trDefSel,
#ec.sprites-plus-png,
#ec.sprites-inbox-png {
    background-color: var(--bg-medium) !important;
}

/* Better date separator */
.gbIL > .gh.fgh {
    height: 24px;
}
.gh {
    font-size: 90% !important;
    bottom: 7px;
}
.ghcnts {
    color: var(--hlight-1) !important;
}

/* Get rid of top bar that takes a lot of space */
#divAlertBarContainer {
    top: 0px;
    border: none !important;
}
#divMainPage {
    border: none !important;
    top: 30px;
}

/* Scrollbars */
#divScrollbar {
    opacity: 0.1 !important;
}
.vlvExpSel * {
    background-color: var(--bg-medium) !important;
}

/* Fix the folding icons on mail details */
.sprites-pipe-end-png,
.sprites-pipe-stop-s-png {
    background-image: none;
}
.sprites-minus-png {
    background-color: var(--bg-medium) !important;
}

/* Fix the content menu */
.cmLnk {
    border: none !important;
}
.cmHiLite * {
    color: var(--hlight-1) !important;
}

/* Consistent button hover from yellow frame to blue text */
.tbfHvr:hover,
.fbBiuBtn:hover,
.alertBtnBorder {
    border-color: var(--bg) !important;
}
.tbfHvr:hover *,
.fbBiuBtn:hover *,
.alertBtnTxt:hover {
    color: var(--hlight-1) !important;
}

/************************************************************************
* CALENDAR VIEW
************************************************************************/
div#divDVEventAreaGradient {
    background: var(--bg-light) !important;
    -moz-box-shadow: 10px 10px 5px 0px rgba(0, 0, 0, 0.75);
}
div#divDVEventContainer:hover {
    height: 20em !important;
}
.calContainer:hover {
    background: var(--hlight-1) !important;
    transition: all 300ms ease-in;
    padding: 1em !important;
}
#spanDayName {
    font-size: 10px !important;
}
#divDVHeader {
    top: 15px !important;
}

/************************************************************************
* LOGIN WINDOW
************************************************************************/
.tdConnImg,
#mdRt,
#mdLft,
.tblLgn {
    display: none;
}
.btn {
    animation: fadein 2s;
}
.btnOnMseOvr {
    color: var(--hlight-1) !important;
    cursor: pointer;
    animation: fadein 2s;
}
@keyframes fadein {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

.ghx-swimlane-header {
    background-color: #313434;
}
.ghx-swimlane-header:after {
    box-shadow: none !important;
}
.ghx-swimlane-header .ghx-heading {
    margin: 4px 0 !important;
    font-size: 80%;
}
.ghx-swimlane-header .ghx-description {
    font-size: 90%;
}
.ghx-issue .ghx-highlighted-fields {
    margin-top: 3px;
}
.ghx-issue .ghx-highlighted-fields * {
    font-size: 11px;
}
#gh,
#ghx-pool,
#ghx-column-headers,
#ghx-detail-view * {
    background-color: #313434 !important;
    color: #e2e4e6 !important;
}
.ghx-group {
    background: #414141 !important;
    color: #e2e4e6 !important;
}
.ghx-parent-stub .ghx-key {
    border-left: 10px solid #414141 !important;
    color: #e2e4e6 !important;
    font-weight: bold !important;
}
.ghx-parent-stub .ghx-summary {
    color: #e2e4e6 !important;
}
.ghx-parent-stub {
    border-top: none;
    background: none;
}
.ghx-issue,
.ghx-issue-content {
    background-color: #393F3F !important;
    border: none;
    border-bottom: 1px solid #696969;
}
.ghx-issue-content {
    padding-bottom: 1px !important;
}
.ghx-days {
    display: none;
}
#ghx-column-headers h2 {
    color: #e2e4e6 !important;
}
.ghx-inner {
    color: #e2e4e6 !important;
}
.js-key-link {
    color: #e2e4e6 !important;
    font-weight: bold;
}
.ghx-column {
    background-color: #313434 !important;
}
.ghx-column.ui-sortable {
    background-color: #414141 !important;
}
.ghx-qty,
.ghx-qty h2 {
    color: #e2e4e6 !important;
}
#ghx-release {
    display: none;
}
#js-work-quickfilters * {
    color: #e2e4e6 !important;
}
.ghx-active {
    background-color: #87AFDF !important;
}
.ghx-column-headers .ghx-column {
    border-bottom: 2px solid #696969;
}
*::-webkit-scrollbar {
    width: 15px;
}
*::-webkit-scrollbar-thumb {
    background: #393F3F;
}
